import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import Question from './question';
import Button from 'react-bootstrap/Button';

const characters = [
  {name: 'CALAMARDO', image:'https://th.bing.com/th/id/OIP.IXJKy_PHCB643c7qI8A4QgHaG3?pid=ImgDet&rs=1', ficticio: true, state:true},
  {name: 'DA VINCI', image:'https://th.bing.com/th/id/OIP.OSCPEfSuDRmWBavSUHfAbgHaI-?pid=ImgDet&rs=1', ficticio: false, state:true},
  {name: 'DR. SIMI', image:'https://i.pinimg.com/originals/bc/09/87/bc09874ea20d57a79ab2606be03f7fb3.jpg', ficticio: true, state:true},
  {name: 'MULÁN', image:'https://th.bing.com/th/id/R.6cf5f0fd277e7865d5b8bea1e2f41231?rik=vNCj%2fEezcAmtuQ&riu=http%3a%2f%2fimages1.fanpop.com%2fimages%2fphotos%2f2400000%2fMulan-Wallpaper-mulan-2471043-1024-768.jpg&ehk=MxKVM1EZa9YmMN%2fGsCInTmaJVKZ32YPClj6uFQR7Wjc%3d&risl=&pid=ImgRaw&r=0', ficticio: true, state:true},
  {name: 'MAMÁ COCO', image:'https://th.bing.com/th/id/OIP._2f7SGaUBPtiS6UBmW_6lQHaFA?pid=ImgDet&rs=1', ficticio: true, state:true},
  {name: 'YOSHI', image:'https://th.bing.com/th/id/OIP.vmoBRq5QFBmGqjahUdzPyQHaLV?pid=ImgDet&rs=1', ficticio: true, state:true},
  {name: 'MICHAEL JACKSON', image:'https://th.bing.com/th/id/R.7bbdd9ce07957c7b12fa4732567e5949?rik=kf5TKRO6EdfgYg&riu=http%3a%2f%2fi.huffpost.com%2fgen%2f1351072%2foriginal.jpg&ehk=l3DNQS5TDQKOINix0fcX%2f1P1aRsIBKGG0%2buDKKx%2fq88%3d&risl=&pid=ImgRaw&r=0', ficticio: false, state:true},
  {name: 'PATRICIO', image:'https://th.bing.com/th/id/OIP.MxZg-aB0gHIleIFSC0zawwHaFW?pid=ImgDet&rs=1', ficticio: true, state:true},
]

function verify(a, b ){
  characters.map((character => 
character.props=b ? character.image = character.image : character.image= 'https://www.pepaferrer.com/wp-content/uploads/2019/07/foto-negra.jpg' 
  ))
}

function Character() {
  return (
    <>
    <Card.Body style={{color:'white', textAlign:'center', marginTop:'10px'}}>¿Tu personaje es ficticio?</Card.Body><br></br>
    <Button onClick={localStorage.getItem('verify(ficticio, false)')} style={{marginLeft:'620px'}} variant="outline-warning">〤</Button>
    <Button onClick={localStorage.getItem('verify(ficticio, true)')} variant="outline-warning">✓</Button>
    (characters.map((character => 
    <Container style={{margin:'0px', marginTop:'60px'}}>
        <Card style={{float:"left",  width:'100px', height:'20', margin:'5px', padding:'0px', backgroundColor:'#e0e0e0', alignItems:'center', textAlign:'center'}}>
     <Card.Img variant="top" width='100px' height='100px' src={character.image} />
      <Card.Body>
        <Card.Title style={{fontSize:'7px'}}>{character.name}</Card.Title>
        </Card.Body>
        </Card>
    </Container>
    ))
    </>
  );
}

export default Character;
