import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

function Question(props) {

function check(){
  props.chars.map((character =>
    character.name==false ? character.image=character.image : character.image='https://www.pepaferrer.com/wp-content/uploads/2019/07/foto-negra.jpg'
    ))
}


  return (   
    <>
      <Card.Body style={{color:'white', textAlign:'center', marginTop:'10px'}}>¿Tu personaje es ficticio?</Card.Body><br></br>
      <Button onClick={check} style={{marginLeft:'620px'}} variant="outline-warning">〤</Button>
      <Button variant="outline-warning">✓</Button>
    </>
  );
}

export default Question;
